const items = $input.all();
if (items.length === 0) return { error: "No data received." };

// 1. Metadata Handling
let datasetName = "Test";
let datasetDesc = "";
let datasetEmail = "";
let datasetAuth = 0;

try {
  const uploadNode = $('Upload Dataset CSV').first().json;
  datasetName = uploadNode.dataset_name || datasetName;
  datasetDesc = uploadNode.description || datasetDesc;
  datasetEmail = uploadNode.email || datasetEmail;
  datasetAuth = uploadNode.auth || datasetAuth;
} catch (e) {}

// 2. STRICTOR TYPE DETECTION
function detectType(val) {
  if (val === null || val === undefined || String(val).trim() === '') return 'null';

  const str = String(val).trim();

  // A. Financial Number Cleaning
  const cleanNum = str.replace(/[$,]/g, '');
  if (cleanNum !== "" && !isNaN(cleanNum)) {
    // If it has a dot, a comma, or is very large, it's 'real'
    if (str.includes('.') || str.includes(',') || Math.abs(parseFloat(cleanNum)) > 999999) {
      return 'real';
    }
    return 'int';
  }

  // B. Strict Date Regex (ISO format or MM/DD/YYYY)
  // This prevents "Budget 2025" from matching.
  const strictDate = /^\d{4}-\d{2}-\d{2}$|^\d{1,2}[\/-]\d{1,2}[\/-]\d{2,4}$/;
  if (strictDate.test(str)) return 'date';

  // C. Boolean
  if (str.toLowerCase() === 'true' || str.toLowerCase() === 'false') return 'bool';

  return 'string';
}

// 3. TYPE UPGRADE LOGIC (Specific -> General)
// If one row is 'int' and another is 'real', the column is 'real'.
// If any row is 'string', the whole column must be 'string'.
function getWinnerType(current, next) {
  if (current === 'null' || !current) return next;
  if (next === 'null') return current;
  if (current === 'string' || next === 'string') return 'string';
  if (current === 'real' || next === 'real') return 'real';
  if (current === 'int' && next === 'int') return 'int';
  if (current === 'date' && next === 'date') return 'date';
  return 'string'; // Default to string on conflict
}

// 4. SCAN ALL ROWS FOR HEADERS AND TYPES
const columnTypes = {}; // { HeaderName: WinnerType }

items.forEach(item => {
  for (const [key, value] of Object.entries(item.json)) {
    const type = detectType(value);
    columnTypes[key] = getWinnerType(columnTypes[key], type);
  }
});

// 5. GENERATE FINAL MAPPING
const counters = { string: 1, real: 1, int: 1, date: 1, bool: 1 };
const mapping = {};
const dictionary = {};

// Sort headers to maintain consistent order
Object.keys(columnTypes).sort().forEach(header => {
  let type = columnTypes[header];
  if (type === 'null') type = 'string'; // Fallback for entirely empty columns

  const dbColumn = `${type}${counters[type]}`;
  mapping[header] = dbColumn;
  dictionary[dbColumn] = {
    original_name: header,
    data_type: type
  };
  counters[type]++;
});

return {
  dataset_name: datasetName.replace(/'/g, "''"),
  dataset_summary: datasetDesc.replace(/'/g, "''"),
  column_mapping: mapping,
  column_dictionary: dictionary,
  total_row_count: items.length
};

// 1. Get metadata once
const mapping = $("Mapping Logic").first().json.column_mapping;
const datasetId = $("Insert Record Manager").first().json.dataset_id;

// 2. Get the actual CSV data
const allRows = $("Extract from CSV").all();

// 3. Batching Logic
const results = [];
const batchSize = 200;

const mapEntries = Object.entries(mapping); 
const dbColumnNames = mapEntries.map(entry => entry[1]);
const csvHeaderKeys = mapEntries.map(entry => entry[0]);

/**
 * NEW: Date Correction Logic
 * Standardizes common date strings to ISO format (YYYY-MM-DD)
 */
const standardizeDate = (val) => {
    const d = new Date(val);
    if (isNaN(d.getTime())) return null; // Invalid date

    // Returns YYYY-MM-DD
    return d.toISOString().split('T')[0];
};

/**
 * UPDATED ESCAPE, SANITIZE & CORRECT LOGIC
 */
const prepareValue = (val, targetColumn) => {
    // 1. Handle Nulls/Empty
    if (val === null || val === undefined || String(val).trim() === '') return 'NULL';

    const stringVal = String(val).trim();
    const colLower = targetColumn.toLowerCase();

    // 2. Numeric Correction (int, real, float, amount, price)
    const isNumericColumn = colLower.startsWith('int') || 
                            colLower.startsWith('real') || 
                            colLower.includes('amount') || 
                            colLower.includes('price');

    if (isNumericColumn) {
        const cleanNum = stringVal.replace(/[,\s$]/g, '');
        if (cleanNum === "" || isNaN(cleanNum)) return 'NULL';
        return cleanNum;
    }

    // 3. Date Correction (date, created_at, received_at, etc.)
    const isDateColumn = colLower.includes('date') || 
                         colLower.includes('_at') || 
                         colLower.includes('time');

    if (isDateColumn) {
        const correctedDate = standardizeDate(stringVal);
        if (!correctedDate) return 'NULL'; // Prevent SQL crash on "garbage" dates
        return `'${correctedDate}'`;
    }

    // 4. Standard string escaping
    return `'${stringVal.replace(/'/g, "''")}'`;
};

// Define column list using the fixed order
// Note: Using double quotes for identifiers is safer in PostgreSQL
const columns = ["dataset_id", "row_index", ...dbColumnNames].map(c => `"${c}"`);

for (let i = 0; i < allRows.length; i += batchSize) {
    const chunk = allRows.slice(i, i + batchSize);

    const values = chunk.map((item, idx) => {
        const row = item.json;

        const rowValues = [
            `'${datasetId}'`,
            i + idx + 1,
            ...mapEntries.map(entry => {
                const csvHeader = entry[0];
                const dbColumn = entry[1];
                return prepareValue(row[csvHeader], dbColumn);
            })
        ];

        return `(${rowValues.join(',')})`;
    }).join(',');

    results.push({
        json: {
            sql: `INSERT INTO n8n_data.universal_datatable (${columns.join(',')}) VALUES ${values};`
        }
    });
}

return results;

