-- 2. Create it fresh with the new column names
CREATE VIEW n8n_data.v_active_analysis AS 
SELECT 
    {{ 
      Object.entries($node["Mapping Logic"].json.column_mapping)
      .map(([name, col]) => {
        const safeName = (name && name.trim() !== "") ? name.trim() : `unnamed_${col}`;
        return `${col} AS "${safeName}"`;
      })
      .join(', ') 
    }}
FROM n8n_data.universal_datatable
WHERE dataset_id = '{{ $node["Insert Record Manager"].json.dataset_id }}';

