const mapping = $json.column_mapping;
const datasetId = $json.dataset_id;

// 1. Build the SELECT clause with safe naming and fallback for empty headers
const selectClauses = Object.entries(mapping).map(([originalName, genericColumn]) => {
    // Sanitize: trim whitespace and provide a fallback if the header is empty
    const cleanName = (originalName && originalName.trim() !== "") 
        ? originalName.trim() 
        : `unnamed_${genericColumn}`;

    // Wrap names in double quotes to handle spaces and special characters
    return `${genericColumn} AS "${cleanName}"`;
});

// 2. Construct the "DROP and CREATE" statement
// Using DROP VIEW IF EXISTS is the only way to handle changing column names 
// or counts without triggering a Postgres schema error.
const sql = `
DROP VIEW IF EXISTS n8n_data.v_active_analysis;

CREATE VIEW n8n_data.v_active_analysis AS 
SELECT 
    ${selectClauses.join(',\n    ')}
FROM n8n_data.universal_datatable
WHERE dataset_id = '${datasetId}';
`;

return {
    view_update_sql: sql
};

