// 1. Get all incoming items
// const items = $input.all();
if (items.length === 0) return [];

// 2. Data Cleaning & Shift Correction
const cleanedItems = items.map(item => {
  const raw = item.json;
  const values = Object.values(raw);
  const headers = Object.keys(raw).filter(k => k !== "");

  // DETECTION: If values outnumber headers, the data has shifted (likely due to a comma in a name)
  if (values.length > headers.length) {
    let corrected = {};
    let valIdx = 0;

    for (let hIdx = 0; hIdx < headers.length; hIdx++) {
      // HEURISTIC: If we are at the second column (usually Last Name) and a shift is active, 
      // we merge the split values and "pull" the rest of the row back to the left.
      if (hIdx === 1 && values.length > headers.length) {
        corrected[headers[hIdx]] = `${values[valIdx]}, ${values[valIdx + 1]}`.trim();
        valIdx += 2; // Move past the "extra" split column
      } else {
        corrected[headers[hIdx]] = values[valIdx];
        valIdx++;
      }
    }
    return { json: corrected };
  }
  return item;
});

// 3. Random Sampling (100 Rows)
let shuffled = [...cleanedItems];
for (let i = shuffled.length - 1; i > 0; i--) {
  const j = Math.floor(Math.random() * (i + 1));
  [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
}

// 4. Return only the 100 sample rows
return shuffled.slice(0, 100);

