// 1. Get the raw response
let rawText = $json.output || ""; 

// Initial cleanup: Remove markdown blocks and trim
let sanitizedText = rawText
  .replace(/```[a-z]*\n?/g, "")
  .replace(/```/g, "")
  .trim();

// Split into sections based on [Header]
const sections = sanitizedText.split(/(?=\[[^\]]+\])/g);

let htmlRows = [];
let mdRows = [];

sections.forEach(section => {
  if (!section.trim()) return;

  let lines = section.trim().split(/\r?\n/);
  // Extract header and remove brackets
  let header = lines.shift().replace(/[\[\]]/g, "").trim();

  // --- Build HTML for this section ---
  let sectionHtml = `<h3 style="color: #2c3e50; border-bottom: 1px solid #eee;">${header}</h3><ul style="list-style: none; padding-left: 0;">`;

  // --- Build Markdown for this section ---
  let sectionMd = `### ${header}\n`;

  lines.forEach(line => {
    if (line.includes(':')) {
      let [key, ...valueParts] = line.split(':');
      let keyTrimmed = key.trim();
      let value = valueParts.join(':').trim().replace(/'/g, "''"); // SQL Escape

      sectionHtml += `<li style="margin-bottom: 5px;"><strong>${keyTrimmed}:</strong> ${value}</li>`;
      sectionMd += `* **${keyTrimmed}:** ${value}\n`;
    } else if (line.trim()) {
      let extra = line.trim().replace(/'/g, "''");
      sectionHtml += `<li style="color: #666; font-style: italic;">${extra}</li>`;
      sectionMd += `* _${extra}_\n`;
    }
  });

  sectionHtml += `</ul>`;
  htmlRows.push(sectionHtml);
  mdRows.push(sectionMd);
});

// Final Assembly
const finalHtml = `<div style="font-family: sans-serif; max-width: 800px;">${htmlRows.join('')}</div>`;
const finalMd = mdRows.join('\n');

return {
  cleaned_html: finalHtml,
  cleaned_md: finalMd
};

